CREATE TABLE posts (
    postId INT NOT NULL AUTO_INCREMENT,
    posttitle VARCHAR(26) NOT NULL,
    content LONGTEXT NOT NULL,
    datetime DATETIME,
    chooseFile LONGBLOB,
    likes INT DEFAULT 0,
    comments INT DEFAULT 0,
    views INT DEFAULT 0,
    userId INT,
    PRIMARY KEY (postId),
    FOREIGN KEY (userId) REFERENCES users(userId)
);


